import "core-js/modules/es.array.push.js";
export default {
  name: 'WelcomePage',
  data() {
    return {
      loading: false
    };
  },
  methods: {
    async goToLogin() {
      this.loading = true;
      try {
        // Add a small delay for better UX
        await new Promise(resolve => setTimeout(resolve, 500));
        this.$router.push({
          name: 'UnifiedLogin'
        });
      } catch (error) {
        console.error('Navigation error:', error);
        // Fallback navigation
        window.location.href = '/login';
      } finally {
        this.loading = false;
      }
    },
    goToRegister() {
      this.$router.push({
        name: 'ClientRegistration'
      });
    }
  }
};
