          params.document_type = this.filters.document_type;
        }

        // Apply status filter if specified (allows filtering by specific status)
        if (this.filters.status) {
          params.status = this.filters.status;
        }
        console.log('ðŸ” RequestHistory: Loading historical requests (completed and cancelled)...');
        console.log('ðŸ“‹ RequestHistory: Params:', params);
        const response = await adminDocumentService.getAllRequests(params);
        console.log('ðŸ“¥ RequestHistory: API response:', response);
        if (response.success) {
          // Filter to show only completed and cancelled requests for historical view
          const allRequests = response.data.requests || [];
          const historicalRequests = allRequests.filter(request => {
            const statusName = request.status_name.toLowerCase();
            return statusName === 'completed' || statusName === 'cancelled';
          });
          this.requests = historicalRequests;
          console.log('âœ… RequestHistory: Loaded historical requests:', this.requests.length, 'requests');
          console.log('ðŸ“Š RequestHistory: Breakdown - Total:', allRequests.length, 'Historical:', historicalRequests.length);
          if (this.requests.length > 0) {
            console.log('ðŸ“‹ RequestHistory: First request:', this.requests[0]);
          }
          this.pagination = {
            currentPage: response.data.pagination?.current_page || 1,
            totalPages: response.data.pagination?.total_pages || 1,
            totalItems: historicalRequests.length,
            // Update to reflect filtered count
            itemsPerPage: response.data.pagination?.per_page || 10
          };
        } else {
          console.error('âŒ RequestHistory: API returned unsuccessful response:', response);
        }
      } catch (error) {
        console.error('âŒ RequestHistory: Failed to load requests:', error);
        const errorData = adminDocumentService.parseError(error);
        this.errorMessage = errorData.message || 'Failed to load requests';
        this.requests = [];
      }
