    filteredRequests() {
      // Ensure requests is always an array
      if (!Array.isArray(this.requests)) {
        return [];
      }
      let filtered = [...this.requests];

      // Apply search filter
      if (this.searchQuery) {
        const query = this.searchQuery.toLowerCase();
        filtered = filtered.filter(request => request.document_type.toLowerCase().includes(query) || request.purpose_category?.toLowerCase().includes(query) || request.id.toString().includes(query));
      }

      // Apply status filter
      if (this.statusFilter) {
        filtered = filtered.filter(request => request.status === this.statusFilter);
      }

      // Apply type filter
      if (this.typeFilter) {
        filtered = filtered.filter(request => request.document_type === this.typeFilter);
      }

      // Apply sorting
      filtered.sort((a, b) => {
        switch (this.sortBy) {
          case 'created_at_desc':
            return new Date(b.created_at) - new Date(a.created_at);
          case 'created_at_asc':
            return new Date(a.created_at) - new Date(b.created_at);
          case 'status':
            return a.status.localeCompare(b.status);
          case 'type':
            return a.document_type.localeCompare(b.document_type);
          default:
            return 0;
        }
      });
      return filtered;
    },
